<script>
    $('document').ready(() => {
        if ($('#popup-android_show').is(':checked')) {
            $('#popup-android_route').prop('disabled', false);
            $('#popup-android_params').attr('style', 'display:block');
            $('#popup-android_redirect_type').prop('disabled', false);
        } else {
            $('#popup-android_route').prop('disabled', true);
            $('#popup-android_params').attr('style', 'display:none');
            $('#popup-android_redirect_type').prop('disabled', true);
        }

        if ($('#popup-web_show').is(':checked')) {
            $('#popup-web_link').prop('disabled', false);
