                cloning.find('#<?= $idname ?>-<?= isset($attributeName) ? $attributeName : "params" ?>-0-1')[0].value = '';
                cloning.appendTo('.dynamic-stuff').show();
                i++;
                attach_add();
                attach_delete();
            });
        }

        $('.delete').click(function() {
            $('.delete').off();
            $(this).closest('.dynamic-element').remove();
            attach_add();
            attach_delete();
        });

        function attach_delete() {
            $('.delete').off();
            $('.delete').click(function() {
                $(this).closest('.dynamic-element').remove();
            });
        }
    });

    $('#popup-android_show').on('change', () => {

        if ($('#popup-android_show').is(':checked')) {
            $('#popup-android_route').prop('disabled', false);
            $('#popup-android_params').attr('style', 'display:block');
            $('#popup-android_redirect_type').prop('disabled', false);
